import React, { useState, useEffect } from "react";
import logo from "./logo.svg";
import "./App.css";
import Web3 from "web3";
import { STOCK_ORACLE_ABI, STOCK_ORACLE_ADDRESS } from "./quotecontract";

const APIKEY = "QIHOUY8RAT3SPAK7";

//www.alphavantage.co/query?function=GLOBAL_QUOTE&symbol=MSFT&apikey=QIHOUY8RAT3SPAK7

function App() {
  const [data, setData] = useState("");
  const [input, setInput] = useState("");
  const [symbol, setSymbol] = useState("MSFT");

  async function getQuotes() {
    const web3 = new Web3("http://localhost:7545");
    const accounts = await web3.eth.getAccounts();
    const stockQuote = new web3.eth.Contract(
      STOCK_ORACLE_ABI,
      STOCK_ORACLE_ADDRESS
    );

    var retval = await stockQuote.methods
      .getStockPrice(web3.utils.fromAscii("AAAA"))
      .call();
    console.log(retval);
  }

  useEffect(() => {
    fetch(
      `https://www.alphavantage.co/query?function=GLOBAL_QUOTE&symbol=${symbol}&apikey=${APIKEY}`
    )
      .then(res => res.json())
      .then(res => {
        setData(res);
      });
  }, [symbol]);
  console.log(data);

  return (
    <div>
      <h1>Stock Prices Oracle</h1>
      <div>
        <input
          className="FormField-Input FormField-Input__Text"
          type="text"
          onChange={setInput}
        />
      </div>
      <div>
        <button> Get Information</button>
      </div>
    </div>
  );
}

export default App;
