import React, { useState, useEffect } from "react";
import logo from "./logo.svg";
import "./App.css";
import Web3 from "web3";
import { STOCK_ORACLE_ABI, STOCK_ORACLE_ADDRESS } from "./quotecontract";

import { makeStyles } from "@material-ui/core/styles";
import TextField from "@material-ui/core/TextField";
import Button from "@material-ui/core/Button";

const APIKEY = "QIHOUY8RAT3SPAK7";

const useStyles = makeStyles(theme => ({
  root: {
    "& > *": {
      margin: theme.spacing(1),
      width: 200
    }
  }
}));

//www.alphavantage.co/query?function=GLOBAL_QUOTE&symbol=MSFT&apikey=QIHOUY8RAT3SPAK7

function App() {
  const classes = useStyles();
  const [data, setData] = useState("");
  const [input, setInput] = useState("");
  const [symbol, setSymbol] = useState("MSFT");

  async function getQuotes() {
    const web3 = new Web3("http://localhost:7545");
    const accounts = await web3.eth.getAccounts();
    const stockQuote = new web3.eth.Contract(
      STOCK_ORACLE_ABI,
      STOCK_ORACLE_ADDRESS
    );

    var retval = await stockQuote.methods
      .getStockPrice(web3.utils.fromAscii("AAAA"))
      .call();
    console.log(retval);
  }

  useEffect(() => {
    fetch(
      `https://www.alphavantage.co/query?function=GLOBAL_QUOTE&symbol=${symbol}&apikey=${APIKEY}`
    )
      .then(res => res.json())
      .then(res => {
        setData(res);
      });
  }, [symbol]);
  console.log(data[0]);

  return (
    <div>
      <h1 style="padding = 10px">Stock Prices Oracle</h1>
      <div>
        <form className={classes.root} noValidate autoComplete="off">
          <TextField
            id="Stock-form"
            label="Stock Symbol"
            variant="outlined"
            size="small"
          />

          <div>
            <Button variant="contained" color="primary">
              Get Quote
            </Button>{" "}
          </div>
        </form>
      </div>
    </div>
  );
}

export default App;
